import React, { useRef, useLayoutEffect } from "react";
import * as am4core from "@amcharts/amcharts4/core";
import * as am4charts from "@amcharts/amcharts4/charts";
import am4themes_animated from "@amcharts/amcharts4/themes/animated";

function App(props) {
  const chart = useRef(null);
  let chartRef = useRef();
  useLayoutEffect(() => {
    am4core.useTheme(am4themes_animated);
    if (!chartRef.current) {
      var chart = am4core.create("barchartdiv", am4charts.XYChart);
      chart.data = [
        {
          country: "Lithuania",
          litres: 501,
        },
        {
          country: "Czechia",
          litres: 301,
        },
        {
          country: "Ireland",
          litres: 201,
        },
        {
          country: "Germany",
          litres: 165,
        },
        {
          country: "Australia",
          litres: 139,
        },
        {
          country: "Austria",
          litres: 128,
        },
        {
          country: "UK",
          litres: 99,
        },
        {
          country: "Belgium",
          litres: 60,
        },
        {
          country: "The Netherlands",
          litres: 50,
        },
      ];

      var categoryAxis = chart.xAxes.push(new am4charts.CategoryAxis());
      categoryAxis.dataFields.category = "date";
      categoryAxis.renderer.grid.template.location = 0;
      categoryAxis.renderer.minGridDistance = 1;
      categoryAxis.renderer.labels.template.fill = am4core.color("#ffffff");

      categoryAxis.renderer.labels.template.adapter.add(
        "dy",
        function (dy, target) {
          if (target.dataItem && target.dataItem.index & (2 == 2)) {
            return dy + 25;
          }
          return dy;
        }
      );

      var valueAxis = chart.yAxes.push(new am4charts.ValueAxis());

      valueAxis.renderer.labels.template.fill = am4core.color("#ffffff");
      // Create series
      var series = chart.series.push(new am4charts.ColumnSeries());
      series.dataFields.valueY = "frequency";
      series.dataFields.categoryX = "date";
      series.name = "PostFreq";
      series.columns.template.tooltipText = "{categoryX}: [bold]{valueY}[/]";
      series.columns.template.fillOpacity = 0.8;

      var columnTemplate = series.columns.template;
      columnTemplate.strokeWidth = 2;
      columnTemplate.strokeOpacity = 1;

      series.heatRules.push({
        target: series.columns.template,
        property: "fill",
        min: am4core.color("lightblue"),
        max: am4core.color("#F0E68C"),
        dataField: "valueY",
      });
    }

    return () => {
      chartRef.current && chartRef.current.dispose();
      chartRef.current = null;
    };
  }, []);
  return (
    <div id="barchartdiv" style={{ width: "100%", height: "250px" }}></div>
  );
}
export default App;
