import {
  Box,
  Button,
  Container,
  Paper,
  TextField,
  Typography,
} from "@mui/material";
import { findAllByTestId } from "@testing-library/react";
import React, { useState } from "react";
import { FlexColCenter } from "../elements";

function SchoolForm(props) {
  const [state, setState] = useState({
    noOfStudents: null,
    totalClasses: null,
    expense: null,
    revenue: null,
    profit: null,
    expenseError: false,
    revenueError: false,
  });
  const [expenseError, setExpenseError] = useState(false);
  const [revenueError, setRevenueError] = useState(false);

  const onChange = (e) => {
    setState({ ...state, [e.target.name]: e.target.value });
  };
  const onFocus = () => {
    setExpenseError(findAllByTestId);
    setRevenueError(findAllByTestId);
  };
  const handleSubmit = () => {
    if (!state.expense) setState({ ...state, [e.target.name]: e.target.value });
    else if (!state.revenue) setRevenueError(true);
    else {
      let calProfit = state.revenue - state.expense;
      setState({ ...state, profit: Math.abs(calProfit) });
    }
  };
  return (
    <>
      <Container maxWidth="sm">
        <Paper
          variant="outlined"
          sx={{ my: { xs: 3, md: 6 }, p: { xs: 5, md: 3 } }}
        >
          <FlexColCenter>
            <Typography variant="h4" color="primary">
              Profit Calculator
            </Typography>
          </FlexColCenter>
          <Box mt={3}>
            <TextField
              id="outlined-basic"
              type="number"
              label="No. Of Students"
              variant="outlined"
              fullWidth
              required
              name="noOfStudents"
              value={state.noOfStudents}
              onChange={onChange}
            />
          </Box>
          <Box mt={3}>
            <TextField
              fullWidth
              id="outlined-basic"
              variant="outlined"
              label="Total Classes"
              required
              type="number"
              name="totalClasses"
              value={state.totalClasses}
              onChange={onChange}
            ></TextField>
          </Box>
          <Box mt={3}>
            <TextField
              fullWidth
              id="outlined-basic"
              variant="outlined"
              label="Expense "
              required
              name="expense"
              type="number"
              value={state.expense}
              onChange={onChange}
              error={expenseError}
              onFocus={onFocus}
            ></TextField>
          </Box>
          <Box mt={3}>
            <TextField
              fullWidth
              id="outlined-basic"
              variant="outlined"
              label="Revenue"
              required
              name="revenue"
              type="number"
              value={state.revenue}
              onChange={onChange}
              error={revenueError}
            ></TextField>
          </Box>
          <FlexColCenter mt={3}>
            <Button variant="contained" onClick={handleSubmit}>
              Calculate
            </Button>
          </FlexColCenter>
          {state.profit && (
            <FlexColCenter mt={3}>
              <Typography color="secondary" variant="p">
                Calculated Profit is: {state.profit}
              </Typography>
            </FlexColCenter>
          )}
        </Paper>
      </Container>
    </>
  );
}

export default SchoolForm;
