import React, { useRef, useLayoutEffect } from "react";
import * as am4core from "@amcharts/amcharts4/core";
import * as am4charts from "@amcharts/amcharts4/charts";
import am4themes_animated from "@amcharts/amcharts4/themes/animated";

function Piechart() {
  let chartRef = useRef();
  useLayoutEffect(() => {
    am4core.useTheme(am4themes_animated);
    var chart = am4core.create("piechartdiv", am4charts.PieChart);
    chart.data = [
      {
        country: "Lithuania",
        litres: 501.9,
      },
      {
        country: "Czech Republic",
        litres: 301.9,
      },
      {
        country: "Ireland",
        litres: 201.1,
      },
      {
        country: "Germany",
        litres: 165.8,
      },
      {
        country: "Australia",
        litres: 139.9,
      },
      {
        country: "Austria",
        litres: 128.3,
      },
      {
        country: "UK",
        litres: 99,
      },
      {
        country: "Belgium",
        litres: 60,
      },
      {
        country: "The Netherlands",
        litres: 50,
      },
    ];

    let pieSeries = piechart.series.push(new am4charts.PieSeries());
    pieSeries.dataFields.value = props.value;
    pieSeries.slices.template.tooltipText = "";
    pieSeries.ticks.template.disabled = true;
    pieSeries.labels.template.fill = "#A2BDCB";
    pieSeries.labels.template.text = "Fake IDs: {value}";
    pieSeries.labels.template.fontSize = 20;
    pieSeries.labels.template.fontWeight = 20;

    pieSeries.alignLabels = false;
    pieSeries.labels.template.radius = am4core.percent(-40);
    pieSeries.labels.template.fill = am4core.color("white");
    pieSeries.dataFields.category = props.category;
    pieSeries.colors.list = [am4core.color("#48A0CE")];
    return () => {
      chartRef.current && chartRef.current.dispose();
      chartRef.current = null;
    };
  }, []);
  return (
    <div id="piechartdiv" style={{ width: "100%", height: "250px" }}></div>
  );
}
export default Piechart;
